import styled from 'styled-components';
import { Button } from 'react-bootstrap';

const Wrapper = styled.div`
  position: fixed;
  left: 0;
  bottom: 0;
  width: 100%;
  text-align: center;
  border-top: 1px solid grey;
`;

const CartItem = styled.div`

`;

const PayButton = styled(Button)`
  background-color: green;
  color: white;
  font-size: 20px;
  padding: 5px 20px;
`
export {
  Wrapper,
  CartItem,
  PayButton,
};
